(9, 'Bachelor of Science in Electronics Engineering', 'BSECE', 'bsece.course.subscription', 'Bachelor of Science in Electronics Engineering (BSECE) is a five-year college program that focuses on the conceptualization, design, and development of electronic, computer and communication products, systems, services and processes.', 0, '2021-03-01 23:04:09', '2021-03-08 16:38:27', NULL),
(10, 'Bachelor of Science in Electrical Engineering', 'BSEE', 'bsee.course.subscription', 'The Bachelor of Science in Electrical Engineering is a five-year degree program that focuses on conceptualizing, developing, and designing a safe, economical, and ethical utilization of electrical energy.', 0, '2021-03-01 23:04:37', '2021-03-08 16:37:29', NULL),
